INSERT INTO `xc_notification_translations` (`label_id`, `id`, `name`, `description`, `customerSubject`, `customerText`, `adminSubject`, `adminText`, `code`) VALUES
(1, 'failed_admin_login', 'Failed admin login', 'The notification is sent to the email address of the admin user for whose account failed login attempts have been detected several times in a row. A copy of the message is also sent to the store’s super admin user.', '', '', 'WARNING! Administrator login failure', 'Failed administrator login detected.', 'en'),
(2, 'low_limit_warning', 'Low limit warning', 'This message is used to notify the administrator when the stock level of a product reaches the low stock limit as specified in the product details.', '', '', 'Low stock warning', 'The inventory level of a product in your store has reached the low limit.', 'en'),
(3, 'order_advanced_changed', 'Order changed with advanced order management', 'This message is sent to notify the customer of any changes made to their order by an administrator using the advanced order management feature.', 'Your order has been changed', 'Your order has been changed by customer service. Here are the details of your order:', '', '', 'en'),
(4, 'order_canceled', 'Order canceled', 'This notification is sent to both the administrator and customer when an order gets the status Canceled.', 'Your order has been canceled', 'Your order has been canceled.', 'Order has been canceled', 'Order has been canceled.', 'en'),
(5, 'order_changed', 'Order changed', 'This notification is sent to both the administrator and customer when the status of an order is updated - in the event that no other type of notification is sent.', 'Your order has been changed', 'Your order has been changed. Here are the details of your order:', 'Order has been changed', 'Order has been changed.', 'en'),
(6, 'order_created', 'Order created', 'This notification is sent to both the administrator and customer when a new order is created (Disabled by default).', 'Order has been created', 'Thank you for shopping with us! We received your order.<br />You will receive an order confirmation once your order is processed.', 'Order has been created', 'Order has been created.', 'en'),
(7, 'order_failed', 'Order declined', 'This notification is sent to both the administrator and customer when an order gets the status Declined.', 'Your order has been declined', 'The status of your order has been changed to "Declined". This could be for one of the following reasons:<br />1) The order was declined by the store administrator.<br />2) The payment could not be processed at the time of order placement.<br />For more information, please contact the store administrator.', 'Order has been declined', 'Order has been declined.', 'en'),
(8, 'order_processed', 'Order processed', 'This notification is sent to both the administrator and customer when an order gets the status Paid.', 'Order confirmation', 'Your order has been processed. Thank you for your business.<br />We will notify you by email of any further order status updates.', 'Order has been processed', 'Order has been processed.', 'en'),
(9, 'order_shipped', 'Order shipped', 'This notification is sent to both the administrator and customer when an order gets the status Shipped.', 'Your order has been shipped', 'Your order has been shipped. Thank you for your business.<br />Please come back soon!', '', '', 'en'),
(10, 'order_tracking_information', 'Order tracking information', 'This notification is sent to the customer when an administrator clicks the "Send tracking information to customer" button.', 'Your order tracking information', 'Here are the tracking details for your order', '', '', 'en'),
(11, 'profile_created', 'User profile created', 'This notification is sent to both the administrator and customer when a new customer profile is created.', 'Welcome!', 'Thank you for registering with us.', 'New user registration', 'A new user profile has been registered:', 'en'),
(12, 'profile_deleted', 'User profile deleted', 'This notification is sent to the administrator when a customer profile is deleted.', '', '', 'User profile has been deleted', 'The following user profile has been deleted:', 'en'),
(13, 'profile_modified', 'User profile modified', 'This notification is sent to both the administrator and customer when a user profile is updated.', 'Your profile has been modified', 'Your profile has been modified. To view your profile information, log in at our site.', 'User profile has been modified', 'The user profile has been modified.', 'en'),
(14, 'recover_password_request', 'Password reset request', 'This notification is sent to the user when a password reset is requested for their account.', 'Confirm password reset request', '<p>You have received this message because you requested to reset your forgotten password.<br />If you did not submit such a request, it might mean that somebody was trying to gain access to your account.</p><p>To confirm that this request was submitted by you, click on the link below<br/>(If the link does not work, please copy and paste the entire URL into your browser''s address bar and press Enter).</p>', 'Confirm password reset request', '<p>You have received this message because you requested to reset your forgotten password.<br />If you did not submit such a request, it might mean that somebody was trying to gain access to your account.</p><p>To confirm that this request was submitted by you, click on the link below<br/>(If the link does not work, please copy and paste the entire URL into your browser''s address bar and press Enter).</p>', 'en'),
(15, 'register_anonymous', 'User account created for guest buyer based on order info', 'This notification is sent to the customer when a customer profile is created for them by the store administrator based on a previously created order.', 'User account created', '<p>Thank you for shopping with us! We noticed that one or more orders you made at our store were placed using guest checkout. We appreciate your business and look forward to continue serving you in the future. For your convenience, we created an account for you at our store. The account stores all the information about your previous orders with us.</p><p>To log in to your account, please use the following credentials:<br />(The password can be changed in your account profile.)</p>', '', '', 'en'),
(16, 'failed_transaction', 'Failed transaction', 'This notification is sent to the administrator if payment transaction was failed', '', '', 'Failed transaction', '', 'en'),
(17, 'modules/CDev/ContactUs/message', 'Contact us form submitted (Contact us module)', 'This notification is sent to the administrator when someone submits a request via the "Contact us" form.', '', '', 'Someone has used the Contact us form', '', 'en'),
(18, 'modules/CDev/ContactUs/message', 'Заполнена форма для связи с администрацией магазина (модуль Contact us)', 'Данное сообщение используется для оповещения администратора в случае поступления в адрес магазина обращения через форму для связи с администрацией.', '', '', 'Поступило обращение через форму для связи с администрацией магазина', '', 'ru'),
(19, 'modules/XC/CanadaPost/return_approved', 'Return request approved (Canada Post module)', 'This notification is sent to the customer if their request to return a product via Canada Post is approved by the administrator.', 'Your return request has been approved', 'Your return request has been approved', '', '', 'en'),
(20, 'modules/XC/CanadaPost/return_approved', 'Запрос на возврат товара одобрен (модуль Canada Post)', 'Данное сообщение отправляется покупателю в случае, если его запрос на возврат товара с пересылкой службой Canada Post получает одобрение администратора.', 'Ваш запрос на возврат товара одобрен', 'Ваш запрос на возврат товара одобрен', '', '', 'ru'),
(21, 'modules/XC/CanadaPost/return_rejected', 'Return request rejected (Canada Post module)', 'This notification is sent to the customer if their request to return a product via Canada Post is rejected by the administrator.', 'Your return request has been rejected', 'Your return request has been rejected', '', '', 'en'),
(22, 'modules/XC/CanadaPost/return_rejected', 'Запрос на возврат товара отклонен (модуль Canada Post)', 'Данное сообщение отправляется покупателю в случае, если его запрос на возврат товара с пересылкой службой Canada Post отклоняется администратором.', 'Ваш запрос на возврат товара отклонен', 'Ваш запрос на возврат товара отклонен', '', '', 'ru');
