var toogleChecked=function(value){jQuery('.update-module-list input[type=checkbox]').attr('checked',value||false)};core.microhandlers.add('Uncheck all updates','.uncheck-all',function(event){jQuery(this).click(function(event){toogleChecked(false)})});core.microhandlers.add('Check all updates','.check-all',function(event){jQuery(this).click(function(event){toogleChecked(true)})})